// components/TrainingMetrics.js
import React, { useState, useEffect } from 'react';
import StrategyVisualizer from './StrategyVisualizer';
console.log('StrategyVisualizer:', StrategyVisualizer);
import './TrainingMetrics.css';


const TrainingMetrics = () => {
  const [metricsData, setMetricsData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [iterations, setIterations] = useState(5000);
  const [trackInterval, setTrackInterval] = useState(100);

  // Fetch metrics data from the server
  const fetchMetrics = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch('/api/metrics');
      if (!response.ok) {
        throw new Error('Failed to fetch metrics data');
      }

      const data = await response.json();
      setMetricsData(data);
    } catch (err) {
      console.error('Error fetching metrics:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Start a new training session
  const startTraining = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch('/api/train', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          iterations,
          track_interval: trackInterval
        }),
      });

      if (!response.ok) {
        throw new Error('Training failed');
      }

      // After training completes, fetch the new metrics
      await fetchMetrics();

    } catch (err) {
      console.error('Error training model:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Fetch metrics when component mounts
  useEffect(() => {
    fetchMetrics();
  }, []);

  return (
    <div className="training-metrics">
      <h2>CFR Training Metrics</h2>

      <div className="training-controls">
        <div className="form-group">
          <label>Training Iterations:</label>
          <input 
            type="number" 
            value={iterations}
            onChange={(e) => setIterations(parseInt(e.target.value) || 1000)}
            min="1000"
            max="100000"
            step="1000"
          />
        </div>

        <div className="form-group">
          <label>Tracking Interval:</label>
          <input 
            type="number" 
            value={trackInterval}
            onChange={(e) => setTrackInterval(parseInt(e.target.value) || 100)}
            min="10"
            max="1000"
            step="10"
          />
        </div>

        <button 
          className="button-primary"
          onClick={startTraining}
          disabled={loading}
        >
          {loading ? 'Training...' : 'Start New Training'}
        </button>

        <button
          className="button-secondary"
          onClick={fetchMetrics}
          disabled={loading}
        >
          Refresh Metrics
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {loading ? (
        <div className="loading">Training in progress... This may take several minutes.</div>
      ) : (
        <StrategyVisualizer metricsData={metricsData} />
      )}
    </div>
  );
};

export default TrainingMetrics;
