// components/PokerTable.js
import React from 'react';
import Card from './Card';
import './PokerTable.css';

const PokerTable = ({ gameHistory, cards, isBotVsBot }) => {
  // Determine if the game is over based on game history
  const isGameOver = gameHistory.length >= 2;

  // Determine if cards should be face up
  const showCards = isGameOver || isBotVsBot;

  return (
    <div className="poker-table">
      <div className="player player-cfr">
        <div className="player-info">
          <div className="player-name">CFR Bot</div>
          <div className="player-action">
            {gameHistory.find(h => h.player === 'cfr')?.action === 'bet' ? 'BET' : 
             gameHistory.find(h => h.player === 'cfr')?.action === 'pass' ? 'PASS' : ''}
          </div>
        </div>
        <Card value={cards?.cfr} faceUp={showCards || isBotVsBot} />
      </div>

      <div className="table-center">
        <div className="table-felt">
          {isGameOver && (
            <div className="game-result">
              {/* Determine and display winner */}
              {gameHistory.length > 0 && gameHistory[gameHistory.length - 1].player === 'cfr' && 
                gameHistory[gameHistory.length - 1].action === 'bet' ? 
                <span className="winner cfr-winner">CFR Bot Wins!</span> : 
                <span className="winner stupid-winner">Stupid Bot Wins!</span>
              }
            </div>
          )}
        </div>
      </div>

      <div className="player player-stupid">
        <Card value={cards?.stupid} faceUp={showCards || isBotVsBot} />
        <div className="player-info">
          <div className="player-name">Stupid Bot</div>
          <div className="player-action">
            {gameHistory.find(h => h.player === 'stupid')?.action === 'bet' ? 'BET' : 
             gameHistory.find(h => h.player === 'stupid')?.action === 'pass' ? 'PASS' : ''}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PokerTable;
