// components/TrainingMetrics.js
import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import './TrainingMetrics.css';
import metricsData from '../data/metrics_history.json';

const TrainingMetrics = () => {
  const [data, setData] = useState(metricsData);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [iterations, setIterations] = useState(5000);
  const [trackInterval, setTrackInterval] = useState(100);
  const [selectedMetric, setSelectedMetric] = useState('strategy');
  const [selectedInfoSet, setSelectedInfoSet] = useState(null);

  // 初始化选中的信息集
  useEffect(() => {
    if (data && data.strategies && Object.keys(data.strategies).length > 0) {
      setSelectedInfoSet(Object.keys(data.strategies)[0]);
    }
  }, [data]);

  // 格式化信息集的辅助函数
  const formatInfoSet = (infoSet) => {
    const cardNames = {
      '1': 'Jack (1)',
      '2': 'Queen (2)',
      '3': 'King (3)'
    };

    const parts = infoSet.split(':');
    const card = cardNames[parts[0]] || `Card ${parts[0]}`;

    let history = '';
    if (parts.length > 1) {
      for (let i = 1; i < parts.length; i++) {
        if (parts[i]) {
          history += ` after ${parts[i].toLowerCase()}`;
        }
      }
    } else {
      history = ' (initial)';
    }

    return `${card}${history}`;
  };

  // 模拟训练过程
  const startTraining = () => {
    setLoading(true);

    // 模拟训练过程的延迟
    setTimeout(() => {
      setLoading(false);
      // 不需要做任何事情，因为我们已经有了静态数据
    }, 1500);
  };

  // 模拟刷新
  const refreshMetrics = () => {
    setLoading(true);

    // 模拟刷新过程的延迟
    setTimeout(() => {
      setLoading(false);
      // 不需要做任何事情，因为我们已经有了静态数据
    }, 800);
  };

  // 准备图表数据
  const getChartData = () => {
    if (!data || !data.iterations || !selectedInfoSet) {
      return [];
    }

    return data.iterations.map((iteration, idx) => {
      if (selectedMetric === 'strategy') {
        const strategy = data.strategies[selectedInfoSet]?.[idx] || [0.5, 0.5];
        return {
          iteration,
          pass: strategy[0],
          bet: strategy[1]
        };
      } else if (selectedMetric === 'regret') {
        const regret = data.regrets[selectedInfoSet]?.[idx] || [0, 0];
        return {
          iteration,
          passRegret: regret[0],
          betRegret: regret[1]
        };
      } else { // expected payoff
        return {
          iteration,
          expectedPayoff: data.expected_payoffs[idx]
        };
      }
    });
  };

  // 获取可选的信息集
  const getInfoSets = () => {
    if (!data) return [];
    return selectedMetric === 'strategy' 
      ? Object.keys(data.strategies || {}) 
      : Object.keys(data.regrets || {});
  };

  return (
    <div className="training-metrics">
      <h2>CFR Training Metrics</h2>



      {error && <div className="error-message">{error}</div>}

      {loading ? (
        <div className="loading">Training in progress... This may take several minutes.</div>
      ) : (
        <div className="strategy-visualizer">
          <div className="visualizer-controls">
            <div className="metric-selector">
              <label>Select Metric:</label>
              <select
                value={selectedMetric}
                onChange={(e) => {
                  setSelectedMetric(e.target.value);
                  // 切换指标时重置选中的信息集
                  const infoSets = e.target.value === 'strategy' 
                    ? Object.keys(data?.strategies || {}) 
                    : Object.keys(data?.regrets || {});
                  if (infoSets.length > 0) {
                    setSelectedInfoSet(infoSets[0]);
                  } else {
                    setSelectedInfoSet(null);
                  }
                }}
              >
                <option value="strategy">Strategy Probabilities</option>
                <option value="regret">Regret Values</option>
                <option value="payoff">Expected Payoff</option>
              </select>
            </div>

            {selectedMetric !== 'payoff' && (
              <div className="info-set-selector">
                <label>Select Information Set:</label>
                <select
                  value={selectedInfoSet || ''}
                  onChange={(e) => setSelectedInfoSet(e.target.value)}
                  disabled={getInfoSets().length === 0}
                >
                  {getInfoSets().map((infoSet) => (
                    <option key={infoSet} value={infoSet}>
                      {formatInfoSet(infoSet)}
                    </option>
                  ))}
                </select>
              </div>
            )}
          </div>

          <div className="chart-container">
            <h3>
              {selectedMetric === 'strategy' && selectedInfoSet
                ? `Strategy Probabilities for ${formatInfoSet(selectedInfoSet)}`
                : selectedMetric === 'regret' && selectedInfoSet
                  ? `Regret Values for ${formatInfoSet(selectedInfoSet)}`
                  : 'Expected Payoff Over Iterations'
              }
            </h3>

            {data && data.iterations && data.iterations.length > 0 ? (
              <ResponsiveContainer width="100%" height={400}>
                <LineChart
                  data={getChartData()}
                  margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
                >
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis 
                    dataKey="iteration" 
                    label={{ value: 'Iterations', position: 'insideBottomRight', offset: -5 }} 
                  />
                  <YAxis 
                    label={{ 
                      value: selectedMetric === 'strategy' 
                        ? 'Probability' 
                        : selectedMetric === 'regret' 
                          ? 'Regret Value' 
                          : 'Expected Payoff', 
                      angle: -90, 
                      position: 'insideLeft' 
                    }} 
                  />
                  <Tooltip />
                  <Legend />

                  {selectedMetric === 'strategy' && (
                    <>
                      <Line 
                        type="monotone" 
                        dataKey="pass" 
                        name="PASS Probability" 
                        stroke="#8884d8" 
                        activeDot={{ r: 8 }} 
                      />
                      <Line 
                        type="monotone" 
                        dataKey="bet" 
                        name="BET Probability" 
                        stroke="#82ca9d" 
                        activeDot={{ r: 8 }} 
                      />
                    </>
                  )}

                  {selectedMetric === 'regret' && (
                    <>
                      <Line 
                        type="monotone" 
                        dataKey="passRegret" 
                        name="PASS Regret" 
                        stroke="#8884d8" 
                        activeDot={{ r: 8 }} 
                      />
                      <Line 
                        type="monotone" 
                        dataKey="betRegret" 
                        name="BET Regret" 
                        stroke="#82ca9d" 
                        activeDot={{ r: 8 }} 
                      />
                    </>
                  )}

                  {selectedMetric === 'payoff' && (
                    <Line 
                      type="monotone" 
                      dataKey="expectedPayoff" 
                      name="Expected Payoff" 
                      stroke="#ff7300" 
                      activeDot={{ r: 8 }} 
                    />
                  )}
                </LineChart>
              </ResponsiveContainer>
            ) : (
              <div className="no-data">No metrics data available. Run training first.</div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default TrainingMetrics;
