// Add this import at the top of App.js
import TrainingVisualizer from './TrainingVisualizer';

// Add this state variable to the App component
const [showTrainingData, setShowTrainingData] = useState(false);

// Add this function to the App component
const toggleTrainingData = () => {
  setShowTrainingData(!showTrainingData);
};

// Add this button to the header of the App component (inside the app-header div)
<button 
  className="button-primary" 
  onClick={toggleTrainingData}
  style={{ marginTop: '10px' }}
>
  {showTrainingData ? 'Hide Training Data' : 'Show Training Data'}
</button>

// Add this right after the header and before the main-content div
{showTrainingData && <TrainingVisualizer />}
