// components/GameHistoryTable.js
import React from 'react';
import './GameHistoryTable.css';

const GameHistoryTable = ({ history }) => {
  if (!history || history.length === 0) {
    return (
      <div className="game-history-container">
        <h3>Game History</h3>
        <div className="no-history">No game history yet. Play a game to see actions here.</div>
      </div>
    );
  }

  return (
    <div className="game-history-container">
      <h3>Game History</h3>
      <table className="game-history-table">
        <thead>
          <tr>
            <th>Round</th>
            <th>Player</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {history.map((item, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{item.player === 'cfr' ? 'CFR Bot' : 'Stupid Bot'}</td>
              <td className={item.action === 'bet' ? 'bet-action' : 'pass-action'}>
                {item.action === 'bet' ? 'BET' : 'PASS'}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default GameHistoryTable;
